 import react, {useState}from 'react'
 import axios from 'axios';



 const cors = require('cors')
 

 function HookComponent(props){
     const [name,setName]=useState({name:'',email:'',password:''});

    
     function submit(e) {


       e.preventDefault();

       console.log(name);
      axios.post('http://localhost/react/reactPhp/index.php/home/insert',{
        email:name.email,
        password:name.password,
        name:name.name,
      })
        .then(res => {
         console.log(res)
        })
    }

 
        return(
            <div>
              <div className="container p-5">

              <form onSubmit={(e)=>submit(e)}>
                <div className="row">
                  <div className="col-md-3"><label for="exampleInputEmail1" className="form-label">Email address</label></div>
                  <div className="col-md-9"><input type="email" value={name.email} className="form-control" onChange={e=>setName({...name,email:e.target.value})}/></div>
                </div>
                <div className="row">
                  <div className="col-md-3 text-right"><label for="exampleInputEmail1" className="form-label">Password</label></div>
                  <div className="col-md-9"><input value={name.password} className="form-control" onChange={e=>setName({...name,password:e.target.value})}/></div>
                </div>
                <div className="row">
                  <div className="col-md-3"><label for="exampleInputEmail1" className="form-label">Name</label></div>
                  <div className="col-md-9"><input type="text" value={name.name} className="form-control" onChange={e=>setName({...name,name:e.target.value})}/></div>
                </div>
               
          
                <button type="submit" className="btn btn-primary">Submit</button>
              </form>

              
          </div>
          

            </div>
        )
    
 }

 export default HookComponent
