 import react, {useState}from 'react'
 import axios from 'axios';




 function HookComponent(props){
     const [name,setName]=useState({name:'',email:'',password:''});
     const cors = require('cors')
    //  app.use(cors())
    
     function submit(e) {
       e.preventDefault();
      axios.post('http://localhost/react/reactPhp/index.php/home/insert',{
        email:name.email,
        password:name.password,
        name:name.name,
      })
        .then(res => {
         console.log(res)
        })
    }

 
        return(
            <div>
              <form onSubmit={(e)=>submit(e)} >
          <label>Email</label>
          <input type="text" value={name.email} onChange={e=>setName({...name,email:e.target.value})}/>
          <br/>
          <label>Password</label>

          <input type="password" value={name.password} onChange={e=>setName({...name,password:e.target.value})}/>
          <br/>
          <label>Name</label>

          <input type="text" value={name.name} onChange={e=>setName({...name,name:e.target.value})}/>
          <br/>
          <button>Submit</button>

          </form>

            </div>
        )
    
 }

 export default HookComponent
