 import react, {useState}from 'react'
 import axios from 'axios';



 const cors = require('cors')
 

 function HookComponent(props){
     const [name,setName]=useState({name:'',email:'',password:''});

    
     function submit(e) {


       e.preventDefault();

       console.log(name);
      axios.post('http://localhost/react/reactPhp/index.php/home/insert',{
        email:name.email,
        password:name.password,
        name:name.name,
      })
        .then(res => {
         console.log(res)
        })
    }

 
        return(
            <div>
              <div className="container">

              <form onSubmit={(e)=>submit(e)}>
                <div className="mb-3">
                  <label for="exampleInputEmail1" className="form-label">Email address</label>
                  <input type="email" value={name.email} className="form-control" onChange={e=>setName({...name,email:e.target.value})}/>
                  <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div>
                </div>
                <div className="mb-3">
                  <label for="exampleInputPassword1" className="form-label">Password</label>
                  <input type="password" value={name.password} className="form-control" onChange={e=>setName({...name,password:e.target.value})}/>
                </div>
                <div className="mb-3">
                  <label for="exampleInputPassword1" className="form-label">Name</label>
                  <input type="text" value={name.name} className="form-control" onChange={e=>setName({...name,name:e.target.value})}/>
                </div>
                
                <button type="submit" className="btn btn-primary">Submit</button>
              </form>

              
          </div>
          

            </div>
        )
    
 }

 export default HookComponent
