import React,{ Component } from 'react';


class Hello extends Component{

    constructor(props){
        super(props)
        this.state={
            'message':"this is state message",
            'user':"this is pranay"
        }
        this.props={'raju':"raju123"};
    }

     changeState(){
this.setState({'message':" this is modified state"})
    }

    changeState1(){
        this.setState((prevState)=>({'message1':prevState.message}))
            }
    render() {
        const {name,email}=this.props;
        const {message,user}=this.state;
        return (
            <div>

                <h1>{message}-{user}-{this.state.message1}</h1>
                
                <h1>{name}-{email}-{user} </h1>
                <button onClick={()=>this.changeState()}>subscribe</button>
                <button onClick={()=>this.changeState1()}>subscribe1</button>
            </div>
        )
    }
}
export default Hello
