 import react, {useState}from 'react'
 import axios from 'axios';



 const cors = require('cors')
 

 function HookComponent(props){
     const [data,setData]=useState({name:'',email:'',password:''});

    
     function submit(e) {


       e.preventDefault();

       console.log(data);
      axios.post('http://localhost/react/reactPhp/index.php/home/insert',{
        email:data.email,
        password:data.password,
        name:data.name,
      })
        .then(res => {
         if(res.status==200){

         }else{

         }
        })
    }

 
        return(
            <div>
              <div className="container p-5">

              <form onSubmit={(e)=>submit(e)}>
                <div className="row pb-1">
                  <div className="col-md-3 text-right"><label  className="form-label">Email address</label></div>
                  <div className="col-md-9"><input type="email" value={data.email} className="form-control" onChange={e=>setData({...data,email:e.target.value})}/></div>
                </div>
                <div className="row pb-1">
                  <div className="col-md-3 text-right"><label  className="form-label">Password</label></div>
                  <div className="col-md-9"><input value={data.password} className="form-control" onChange={e=>setData({...data,password:e.target.value})}/></div>
                </div>
                <div className="row pb-1">
                  <div className="col-md-3 text-right"><label  className="form-label">Name</label></div>
                  <div className="col-md-9"><input type="text" value={data.name} className="form-control" onChange={e=>setData({...data,name:e.target.value})}/></div>
                </div>
               
              <div className="text-left" style={{textAlign:'center'}}>
              <button type="submit" className="btn btn-primary btn-lg" >Submit</button>
              </div>
              </form>

              
          </div>
          

            </div>
        )
    
 }

 export default HookComponent
