import React, {useState,useEffect} from 'react'
import axios from 'axios'
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
  } from "react-router-dom";
  import Edit from '../components/Edit'




function Users() {
    const [result,setResult]=useState([]);
    
  useEffect(()=>{
    axios.get('http://localhost/react/reactPhp/index.php/home/get_data').then(res=>{
        
        setResult(res.data);
    })
    },[result])


    return (
        <div>
            <h1>Users</h1>
            <table className="table table-bordered">
             <thead>
                 <tr>
                 <th>Name</th>
                 <th>Email</th>
                 <th>Action</th>
                 </tr>
            </thead> 
            <tbody>
            {result.map(tool=>(<tr><td>{tool.name}</td><td>{tool.email}</td><td><Router><Link to={{pathname: `/edit/${tool.id}`}}>Edit</Link><Route path="/edit" component={Edit}></Route></Router></td></tr>))}
            </tbody>  
            </table>
           
           
        </div>
    )
}

export default Users
